from pipe.am.project import Project
node = kwargs.get('node', None)
if node:
	if node.isLockedHDA():
		if node.type().definition() is not None:
			node_path = node.path()
			name = node_path.split('/')[2]
			name = name.lower()
			if name in Project().list_tools():
				return 1
				print("This is the Cloner Tool MenuItem")
return 0

